import React, { Component } from 'react'

class Forms extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
    };
  }


  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    });
  }

  render() {
    return (
      <div>
        <h1>Preencha os dados do Atleta</h1>
        <form>
          <label>
              <span>Nome</span>
              <textarea name="name" value={this.state.name} onChange={this.handleChange} />
          </label>

          <label>
            <span>Numero da Camiseta</span>
            <input
              type="number"
              name="shirtNumber"
            />
          </label>

          <label>
            <span>Já participou da competição antes?</span>
            <input
              type="checkbox"
              name="vaiComparecer"
            />
          </label>
        </form>
      </div>
    );
  }
}

export default Forms;
